import React, { useState, useEffect } from "react";
import "./main.css";
import "./App.css";
// const images = require.context("../public", true);

function App() {
    const [data, setData] = useState([]);
    const [number, setNumber] = useState("");
    const [imageExists, setImageExists] = useState(true);
    // todo: implement a function that reads the public folder and checks if the image exists
    const staticFileList = ["cheese", "cheese2", "cheese3"]; // Define your static list of files here
    const [id_list, setIdList] = useState([]);

    useEffect(() => {
        // Function to run when the component mounts (app starts)
        getPosts();
    }, []); // Empty dependency array means this effect runs only once

    // updated getPosts function
    function handleClick() {
        const breakfastData = {
            name: "cheese",
            colour: "yellow",
            pricePerKg: 10.0,
            imgUrl: "www",
        };
        fetch("https://localhost:7089/breakfasts", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(breakfastData),
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                } else {
                    response.json().then((json) => setData(json));
                    console.log("Response: ", data.id);

                    if (data.id) {
                        setIdList([...id_list, data.id]);
                        console.log("Id list: ", id_list);
                    }
                }
                // Do something if the request is successful
            })
            .catch((error) => {
                console.error(
                    "There was a problem with your fetch operation:",
                    error
                );
            });
    }

    function getPosts() {
        fetch(
            "https://localhost:7089/breakfasts/a07898b0-fffa-439c-944e-216893ddec8e"
        )
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                return response.json();
            })
            .then((json) => {
                setData(json);
                if (json.name && staticFileList.includes(json.name)) {
                    setImageExists(true);
                } else {
                    setImageExists(false);
                }
            })
            .catch((error) => {
                if (error.message === "Failed to fetch") {
                    console.error(
                        "Error: Failed to fetch. Is the server running?"
                    );
                    // Handle the error, show a message to the user, etc.
                } else {
                    console.error(
                        "There was a problem with your fetch operation:",
                        error
                    );
                }
            });
    }

    const handleChange = (event) => {
        // Ensure only numbers are entered
        const value = event.target.value.replace(/\D/g, "");
        setNumber(value);
    };

    return (
        <div className="container">
            <h1 className="title">HTTP Request</h1>
            <button className="btn" onClick={handleClick}>
                Get all posts
            </button>
            <button className="btn" onClick={getPosts}>
                Get posts
            </button>
            <input
                type="text"
                value={number}
                onChange={handleChange}
                placeholder="Enter a number"
            />
            {console.log(data.id)}

            {/* todo implement a condition that makes sense instead of data.id */}
            {data.id && (
                <ul>
                    cheese: {data.name}, colour: {data.colour}, Total Price: $
                    {(data.pricePerKg * number) / 1000}, img:
                    {imageExists && (
                        <img src={"/" + data.name + ".png"} alt="xdd"></img>
                    )}
                </ul>
            )}
        </div>
    );
}

export default App;
