import "./main.css";
// import logo from './logo.svg';
import "./App.css";
import { useState } from "react";

// data: id, name, colour, pricePerKg, imgUrl

function App() {
    const [data, setData] = useState([]);

    function getPosts() {
        fetch(
            "https://localhost:7089/breakfasts/77a5092b-0afd-4eb4-914d-e1a9ab1bc45b"
        )
            .then((response) => response.json())
            // .then((json) => console.log(json));
            .then((json) => setData(json));
    }

    function NumberInput() {
        const [number, setNumber] = useState("");

        const handleChange = (event) => {
            // Ensure only numbers are entered
            const value = event.target.value.replace(/\D/g, "");
            setNumber(value);
        };
    }

    return (
        <div className="container">
            <h1 className="title">HTTP Request</h1>
            <button className="btn" onClick={getPosts}>
                Get posts
            </button>
            {console.log(data.id)}
            {
                <ul>
                    cheese: {data.name}, Total Price: ${data.pricePerKg * 4},
                    img: {data.imgUrl}
                </ul>
            }
        </div>
    );
}

export default App;
