import React, { useState } from "react";

const BreakfastTableRow = ({ formData, imageExists }) => {
    const [quantity, setQuantity] = useState("");

    const handleInputChange = (event) => {
        const value = event.target.value.replace(/\D/g, "");
        setQuantity(value);
    };

    return (
        <tr key={formData.id}>
            <td>{formData.name}</td>
            <td>{formData.colour}</td>
            <td>
                <div>${formData.pricePerKg}</div>
                <div>
                    <input
                        type="text"
                        value={quantity}
                        onChange={handleInputChange}
                        placeholder="Enter a number (grams)"
                    />
                </div>
                <div>Total Price: ${(formData.pricePerKg * quantity) / 1000}</div>
            </td>
            <td>
                {imageExists && <img src={`/${formData.name}.png`} alt="file not found" />}
            </td>
        </tr>
    );
};

const BreakfastTable = ({ formDataList }) => {
    const [makeTable, setMakeTable] = useState(false);
    const [imageExists, setImageExists] = useState(true);

    const generateTable = () => {
        setMakeTable(!makeTable);
    };

    return (
        <>
            <button className="btn" onClick={generateTable}>
                Make Table
            </button>
            {makeTable && (
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Colour</th>
                            <th>Price Per Kg</th>
                            <th>Image</th>
                        </tr>
                    </thead>
                    <tbody>
                        {formDataList.map((formData) => (
                            <BreakfastTableRow
                                key={formData.id}
                                formData={formData}
                                imageExists={imageExists}
                            />
                        ))}
                    </tbody>
                </table>
            )}
        </>
    );
};

export default BreakfastTable;
