import React, { useState } from "react";
import "./main.css";

function App() {
    const [data, setData] = useState([]);
    const [number, setNumber] = useState("");

    function getPosts() {
        fetch(
            "https://localhost:7089/breakfasts/77a5092b-0afd-4eb4-914d-e1a9ab1bc45b"
        )
            .then((response) => response.json())
            .then((json) => setData(json));
    }

    const handleChange = (event) => {
        // Ensure only numbers are entered
        const value = event.target.value.replace(/\D/g, "");
        setNumber(value);
    };

    return (
        <div className="container">
            <h1 className="title">HTTP Request</h1>
            <button className="btn" onClick={getPosts}>
                Get posts
            </button>
            <input
                type="text"
                value={number}
                onChange={handleChange}
                placeholder="Enter a number"
            />
            {console.log(data.id)}
            {
                <ul>
                    cheese: {data.name}, Total Price: $
                    {(data.pricePerKg * number) / 1000}, img: {data.imgUrl}
                </ul>
            }
        </div>
    );
}

export default App;
