/**
 * Component for creating breakfast items.
 * This component includes a form with fields for entering the name, color, and price per kg of a breakfast item.
 * Upon submission, the form data is sent to a server endpoint.
 * If the submission is successful, the form is reset.
 * If there's an error during submission, it's logged to the console.
 *
 * @component
 * @example
 * // Usage:
 * import CreatePage from './CreatePage';
 * const App = () => {
 *   return (
 *     <div>
 *       <CreatePage />
 *     </div>
 *   );
 * }
 * @returns {JSX.Element} The JSX element representing the CreatePage component.
 */

import { useState } from "react";
function CreatePage() {
    // State to manage form data
    const [formData, setFormData] = useState({
        name: "",
        color: "",
        pricePerKg: "", // Changed
        imgUrl: "", // Added
    });

    // Function to handle changes in form fields
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    // Function to handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent default form submission behavior
        try {
            // Send form data to the server
            const response = await fetch("https://localhost:7089/breakfasts", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    ...formData,
                    pricePerKg: parseFloat(formData.pricePerKg), // Changed
                    imgUrl: formData.imgUrl.includes("www")
                        ? formData.imgUrl
                        : "", // Changed
                }),
            });

            // Check if response is not OK (status code >= 400)
            if (!response.ok) {
                throw new Error("Failed to submit form");
            }

            // Log success message if submission is successful
            console.log("Form submitted successfully");

            // Reset form data after successful submission
            setFormData({
                name: "",
                color: "",
                pricePerKg: "",
                imgUrl: "", // Added
            });
        } catch (error) {
            // Log error if there's an issue with form submission
            console.error("Error submitting form:", error);
        }
    };

    // Render the form
    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label htmlFor="name">Name:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label htmlFor="color">Color:</label>
                <input
                    type="text"
                    id="color"
                    name="color"
                    value={formData.color}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label htmlFor="pricePerKg">Price per kg:</label>
                <input
                    type="number"
                    id="pricePerKg"
                    name="pricePerKg"
                    value={formData.pricePerKg}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                {" "}
                {/* Added */}
                <label htmlFor="imgUrl">Image URL:</label>
                <input
                    type="text"
                    id="imgUrl"
                    name="imgUrl"
                    value={formData.imgUrl}
                    onChange={handleChange}
                    required
                />
            </div>{" "}
            {/* Added */}
            <button type="submit">Submit</button>
        </form>
    );
}

export default CreatePage;
