import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import BreakfastTable from "./components/BreakfastTable";
import CreatePage from "./components/CreatePage";
import getBreakfastMockData from "./utils/breakfastMockData";
import "./assets/styles/main.css";
import "./assets/styles/App.css";

function App() {
    const [formDataList, setFormDataList] = useState([]);
    const [counter, setCounter] = useState(1);

    useEffect(() => {
        const fetchData = async () => {
            if (formDataList.length < 5) {
                updateCounter();
                const data = getBreakfastMockData(counter);
                await handleClick(data);
            }
        };

        fetchData();
    }, [formDataList, counter]);

    const updateCounter = () => {
        setCounter((prevCounter) => (prevCounter === 5 ? 1 : prevCounter + 1));
    };

    const handleClick = async (bodyData) => {
        try {
            const response = await fetch("https://localhost:7089/breakfasts", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(bodyData),
            });

            if (!response.ok) {
                throw new Error("Network response was not ok");
            }

            const json = await response.json();
            setFormDataList((prevList) => [...prevList, json]);
        } catch (error) {
            console.error("There was a problem with your fetch operation:", error);
        }
    };

    return (
        <div className="container">
            <h1 className="title">HTTP Request</h1>
            <Link to="/create-page">Create Page</Link>
            <Router>
                <Routes>
                    <Route path="/create-page" element={<CreatePage />} />
                </Routes>
            </Router>
            <BreakfastTable formDataList={formDataList} />
        </div>
    );
}

export default App;
