import React, { useState, useEffect } from "react";
import "./main.css";
import "./App.css";
// const images = require.context("../public", true);

function App() {
    const [formDataList, setFormDataList] = useState([]);
    const [number, setNumber] = useState("");
    const [imageExists, setImageExists] = useState(true);
    // todo: implement a function that reads the public folder and checks if the image exists
    const staticFileList = ["cheese", "cheese2", "cheese3"]; // Define your static list of files here
    const [idList, setIdList] = useState([]);
    const [makeTable, setMakeTable] = useState(false);

    useEffect(() => {
        // Function to continuously run handleClick until idList has 5 elements
        const runHandleClick = () => {
            if (idList.length < 6) {
                handleClick();
            }
        };

        runHandleClick(); // Call the function initially

        return () => {
            // Clean up to avoid memory leaks
            // This will stop handleClick from running after the component unmounts
            // Note: This return function runs when the component unmounts or idList changes
        };
        // eslint-disable-next-line
    }, [idList]); // This effect depends on idList changes

    // updated getPosts function
    function handleClick() {
        const breakfastData = {
            name: "cheese",
            colour: "yellow",
            pricePerKg: 10.0,
            imgUrl: "www",
        };
        fetch("https://localhost:7089/breakfasts", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(breakfastData),
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                } else {
                    response.json().then((json) => {
                        setFormDataList([...formDataList, json]);
                        setIdList([...idList, json.id]);
                    });
                }
            })
            .catch((error) => {
                console.error(
                    "There was a problem with your fetch operation:",
                    error
                );
            });
    }

    function getPosts() {
        fetch(
            "https://localhost:7089/breakfasts/a07898b0-fffa-439c-944e-216893ddec8e"
        )
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                return response.json();
            })
            .then((json) => {
                setData(json);
                if (json.name && staticFileList.includes(json.name)) {
                    setImageExists(true);
                } else {
                    setImageExists(false);
                }
            })
            .catch((error) => {
                if (error.message === "Failed to fetch") {
                    console.error(
                        "Error: Failed to fetch. Is the server running?"
                    );
                    // Handle the error, show a message to the user, etc.
                } else {
                    console.error(
                        "There was a problem with your fetch operation:",
                        error
                    );
                }
            });
    }

    function generateTable() {
        setMakeTable(true);
    }

    const handleChange = (event) => {
        // Ensure only numbers are entered
        const value = event.target.value.replace(/\D/g, "");
        setNumber(value);
    };

    return (
        <div className="container">
            <h1 className="title">HTTP Request</h1>
            <button className="btn" onClick={handleClick}>
                Get all posts
            </button>
            <button className="btn" onClick={getPosts}>
                Get posts
            </button>
            <button className="btn" onClick={generateTable}>
                Make Table
            </button>

            <input
                type="text"
                value={number}
                onChange={handleChange}
                placeholder="Enter a number"
            />
            {console.log(data.id)}

            {/* todo implement a condition that makes sense instead of data.id */}
            {data.id && (
                <ul>
                    cheese: {data.name}, colour: {data.colour}, Total Price: $
                    {(data.pricePerKg * number) / 1000}, img:
                    {imageExists && (
                        <img src={"/" + data.name + ".png"} alt="xdd"></img>
                    )}
                </ul>
            )}

            {makeTable && (
                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Name</th>
                            <th>Colour</th>
                            <th>Price Per Kg</th>
                            <th>Image</th>
                        </tr>
                    </thead>
                    <tbody>
                        {idList.map((id) => (
                            <tr key={id}>
                                <td>{id}</td>
                                <td>{data.name}</td>
                                <td>{data.colour}</td>
                                <td>
                                    <div>${data.pricePerKg}</div>
                                    <div>
                                        <input
                                            type="text"
                                            value={number}
                                            onChange={handleChange}
                                            placeholder="Enter a number"
                                        />
                                    </div>
                                    <div>
                                        Total Price: $
                                        {(data.pricePerKg * number) / 1000}
                                    </div>
                                </td>
                                <td>
                                    {imageExists && (
                                        <img
                                            src={"/" + data.name + ".png"}
                                            alt="xdd"
                                        ></img>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default App;
